<?php
// Koneksi ke database
include 'koneksi.php';

// Ambil data paket berdasarkan ID
$id = $_GET['id']; // Mengambil nilai ID dari URL
$sql = "SELECT * FROM paket WHERE id = $id"; // Query SQL untuk mengambil data paket berdasarkan ID
$result = $conn->query($sql); // Menjalankan query SQL

// Memeriksa apakah data ditemukan
if ($result->num_rows > 0) {
    $row = $result->fetch_assoc(); // Mengambil baris data dari hasil query
    $nama_paket = $row['nama_paket']; // Mengisi variabel dengan nilai nama paket dari database
    $harga = $row['harga']; // Mengisi variabel dengan nilai harga dari database
    $deskripsi = $row['deskripsi']; // Mengisi variabel dengan nilai deskripsi dari database
    $gambar_lama = $row['gambar']; // Mengisi variabel dengan nilai gambar dari database
} else {
    echo "Data tidak ditemukan."; // Menampilkan pesan jika data tidak ditemukan
    exit(); // Menghentikan eksekusi skrip
}

// Proses update data jika form disubmit
if ($_SERVER['REQUEST_METHOD'] === 'POST') {
    $nama_paket = $_POST['nama_paket']; // Mengambil nilai nama paket dari form
    $harga = $_POST['harga']; // Mengambil nilai harga dari form
    $deskripsi = $_POST['deskripsi']; // Mengambil nilai deskripsi dari form
    $gambar_baru = $_FILES['gambar']['name']; // Mengambil nama file gambar baru dari form

    // Pindahkan file gambar baru jika ada
    if (!empty($gambar_baru)) {
        $target_dir = "assets/img/"; // Direktori tempat menyimpan file gambar
        $target_file = $target_dir . basename($gambar_baru); // Path lengkap untuk file gambar baru
        move_uploaded_file($_FILES["gambar"]["tmp_name"], $target_file); // Memindahkan file gambar baru ke direktori target
    } else {
        $gambar_baru = $gambar_lama; // Menggunakan gambar lama jika tidak ada gambar baru yang diunggah
    }

    // Update data paket di database
    $sql = "UPDATE paket SET nama_paket = '$nama_paket', harga = '$harga', deskripsi = '$deskripsi', gambar = '$gambar_baru' WHERE id = $id"; // Query SQL untuk memperbarui data paket

    // Eksekusi query SQL untuk memperbarui data paket
    if ($conn->query($sql) === TRUE) {
        echo "Data paket berhasil diupdate."; // Menampilkan pesan jika update berhasil
        header("Location: pakettampil.php"); // Mengalihkan pengguna ke halaman pakettampil.php setelah berhasil update
        exit(); // Menghentikan eksekusi skrip
    } else {
        echo "Error: " . $sql . "<br>" . $conn->error; // Menampilkan pesan error jika query gagal dieksekusi
    }
}
?>


// Tutup koneksi
$conn->close();
?>
